func (q *Queries) QueryAssets(ctx context.Context, arg QueryAssetsParams) ([]QueryAssetsRow, error) {
	rows, err := q.db.QueryContext(ctx, queryAssets,
		arg.AssetIDFilter,
		arg.TweakedScriptKey,
		arg.AnchorPoint,
		arg.Leased,
		arg.Now,
		arg.MinAnchorHeight,
		arg.MinAmt,
		arg.Spent,
		arg.KeyGroupFilter,
		arg.AnchorUtxoID,
		arg.GenesisID,
		arg.ScriptKeyID,
	)
	if err != nil {
		return nil, err
	}
	defer rows.Close()
	var items []QueryAssetsRow
	for rows.Next() {
		var i QueryAssetsRow
		if err := rows.Scan(
			&i.AssetPrimaryKey,
			&i.GenesisID,
			&i.Version,
			&i.Spent,
			&i.ScriptKeyTweak,
			&i.TweakedScriptKey,
			&i.ScriptKeyRaw,
			&i.ScriptKeyFam,
			&i.ScriptKeyIndex,
			&i.TapscriptRoot,
			&i.WitnessStack,
			&i.TweakedGroupKey,
			&i.GroupKeyRaw,
			&i.GroupKeyFamily,
			&i.GroupKeyIndex,
			&i.ScriptVersion,
			&i.Amount,
			&i.LockTime,
			&i.RelativeLockTime,
			&i.AssetID,
			&i.AssetTag,
			&i.MetaHash,
			&i.GenesisOutputIndex,
			&i.AssetType,
			&i.GenesisPrevOut,
			&i.AnchorTx,
			&i.AnchorTxid,
			&i.AnchorBlockHash,
			&i.AnchorBlockHeight,
			&i.AnchorOutpoint,
			&i.AnchorTapscriptSibling,
			&i.AnchorMerkleRoot,
			&i.AnchorTaprootAssetRoot,
			&i.AnchorLeaseOwner,
			&i.AnchorLeaseExpiry,
			&i.AnchorInternalKey,
			&i.SplitCommitmentRootHash,
			&i.SplitCommitmentRootValue,
		); err != nil {
			return nil, err
		}
		items = append(items, i)
	}
	if err := rows.Close(); err != nil {
		return nil, err
	}
	if err := rows.Err(); err != nil {
		return nil, err
	}
	return items, nil
}

const setAssetSpent = `-- name: SetAssetSpent :one
WITH target_asset(asset_id) AS (
    SELECT assets.asset_id
    FROM assets
    JOIN script_keys
      ON assets.script_key_id = script_keys.script_key_id
    JOIN genesis_assets
      ON assets.genesis_id = genesis_assets.gen_asset_id
    WHERE script_keys.tweaked_script_key = $1
     AND genesis_assets.asset_id = $2
    -- TODO(guggero): Fix this by disallowing multiple assets with the same
    -- script key!
    LIMIT 1
)
UPDATE assets
SET spent = TRUE
WHERE asset_id = (SELECT asset_id FROM target_asset)
RETURNING assets.asset_id
`

